import mlflow
from sklearn import metrics

import numpy as np
from sklearn.metrics import multilabel_confusion_matrix

from sklearn.metrics import confusion_matrix, ConfusionMatrixDisplay
import matplotlib.pyplot as plt


def log_metrics(targets, outputs, label: str = None, n_samples=None):
    """Log train, valid, test metrics

    Args:
        label: choose name of subset like: 'train'| 'valid' | 'test'
    """
    precision = metrics.precision_score(targets, outputs, average="weighted")
    recall = metrics.recall_score(targets, outputs, average="weighted")
    conf_matrix = metrics.multilabel_confusion_matrix(targets, outputs)
    f1_score_micro = metrics.f1_score(targets, outputs, average="micro")
    f1_score_macro = metrics.f1_score(targets, outputs, average="macro")

    label_columns = [
        f"{label}_toxic",
        f"{label}_severe_toxic",
        f"{label}_obscene",
        f"{label}_threat",
        f"{label}_insult",
        f"{label}_identity_hate",
    ]


conf_matrix = [np.random.randint(0, 10, (2, 2)) for _ in range(6)]

label_columns = ["class1", "class2", "class3", "class4", "class5", "class6"]

# Create a 2x3 grid for subplots
fig, axes = plt.subplots(2, 3, figsize=(15, 10))

for i, matrix in enumerate(conf_matrix):
    row = i // 3  # Determine the row index for the subplot
    col = i % 3  # Determine the column index for the subplot

    # Plot the binary confusion matrix in the corresponding subplot
    ax = axes[row, col]
    ax.imshow(matrix, cmap="Blues", interpolation="nearest")

    # Add labels and title to the subplot
    ax.set_xticks([0, 1])
    ax.set_yticks([0, 1])
    ax.set_xticklabels(["Negative", "Positive"])
    ax.set_yticklabels(["Negative", "Positive"])
    ax.set_xlabel("Predicted")
    ax.set_ylabel("True")
    ax.set_title(label_columns[i])
    print(label_columns[i])
    print(matrix)
    # n_subsamples = sum(sum(conf_matrix[i]))
    # each_type = conf_matrix[i][0, 1] + conf_matrix[i][1, 1]
    # percentage = round(each_type / n_subsamples, 2)

# Adjust layout and spacing between subplots
plt.tight_layout()

# Display the plot
plt.show()

